mapper.select:
select ord_code, MBSP_ID, ORD_NAME, ORD_TEL, ORD_PRICE, ORD_REGDATE, ORD_STATUS, PAYMENT_STATUS
from (
        select /*+ index_desc(ORDER_TBL PK_ORDER_TBL) */ rownum rn,ord_code, MBSP_ID, ORD_NAME, ORD_TEL, ORD_PRICE, ORD_REGDATE, ORD_STATUS, PAYMENT_STATUS
from ORDER_TBL
where



 rownum<=#{pageNum} * #{amount}
)
where rn>(#{pageNum}-1) * #{amount}
