mapper.select:
select rn,pro_num, CG_CODE, PRO_NAME, PRO_PRICE, PRO_DISCOUNT, PRO_PUBLISHER, PRO_CONTENT, PRO_UP_FOLDER, PRO_IMG, PRO_AMOUNT, PRO_BUY, PRO_DATE, PRO_UPDATEDATE
from (
        select /*+ index_desc(product_tbl pk_num) */ rownum rn,pro_num, CG_CODE, PRO_NAME, PRO_PRICE, PRO_DISCOUNT, PRO_PUBLISHER, PRO_CONTENT, PRO_UP_FOLDER, PRO_IMG, PRO_AMOUNT, PRO_BUY, PRO_DATE, PRO_UPDATEDATE
from product_tbl
where



 rownum<=#{pageNum} * #{amount}
)
where rn>(#{pageNum}-1) * #{amount}

mapper.update:
update PRODUCT_TBL 
set 
pro_price =#{pro_price}, pro_buy=#{pro_buy} 
where 
pro_num=#{pro_num}

update.foreach:
update PRODUCT_TBL 
set 
pro_price =#{item.pro_price}, pro_buy=#{item.pro_buy} 
where 
pro_num=#{item.pro_num}
