mapper.select:
select pro_num, CG_CODE, PRO_NAME, PRO_PRICE, PRO_DISCOUNT, PRO_PUBLISHER, PRO_CONTENT, PRO_UP_FOLDER, PRO_IMG, PRO_AMOUNT, PRO_BUY, PRO_DATE, PRO_UPDATEDATE
from (
        select /*+ index_desc(product_tbl PK_PRO_NUM) */ rownum rn,pro_num, CG_CODE, PRO_NAME, PRO_PRICE, PRO_DISCOUNT, PRO_PUBLISHER, PRO_CONTENT, PRO_UP_FOLDER, PRO_IMG, PRO_AMOUNT, PRO_BUY, PRO_DATE, PRO_UPDATEDATE
from product_tbl
where


cg_code =#{cg_code}
and
 rownum<=#{cri.pageNum} * #{cri.amount}

)
where rn>(#{cri.pageNum}-1) * #{cri.amount}
